import { emitEffect } from '@devvit/shared-types/client/emit-effect.js';
export function showToast(textOrToast) {
    let toast;
    if (textOrToast instanceof Object) {
        toast = {
            text: textOrToast.text,
            appearance: textOrToast.appearance === 'success'
                ? 1
                : 0,
        };
    }
    else {
        toast = {
            text: textOrToast,
        };
    }
    void emitEffect({
        showToast: {
            toast,
        },
        type: 4,
    });
}
